    $slug = strtolower($service->slug ?? '');
    $name = strtolower($service->title ?? '');
    $iconUrl = strtolower($service->icon_url ?? '');
    
    // Cari icon yang cocok
    $icon = null;
    
    // Prioritaskan icon_url dari database
    if (!empty($iconUrl) && isset($iconMap[$iconUrl])) {
        $icon = $iconMap[$iconUrl];
    } else {
        // Fallback ke keyword matching
        foreach ($iconMap as $keyword => $svgIcon) {
            if (str_contains($slug, $keyword) || str_contains($name, $keyword)) {
                $icon = $svgIcon;
                break;
            }
        }
    }
    
    // Gunakan icon custom jika ada, atau icon dari mapping, atau default
    $finalIcon = $service->icon_svg ?? $icon ?? '<svg xmlns="http://www.w3.org/2000/svg" width="28" height="28" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"><rect x="2" y="3" width="20" height="14" rx="2"/><path d="M8 21h8"/><path d="M12 17v4"/></svg>';
